--
-- Структура таблицы `goods`
--

CREATE TABLE `goods` (
  `id` int(11) NOT NULL COMMENT 'ID',
  `title` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT 'Назва',
  `keywords` text COLLATE utf8_unicode_ci COMMENT 'META keywords',
  `description` text COLLATE utf8_unicode_ci COMMENT 'META description',
  `articul` varchar(25) COLLATE utf8_unicode_ci NOT NULL COMMENT 'Артикул',
  `url_key` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT 'Ключ',
  `author` varchar(300) COLLATE utf8_unicode_ci DEFAULT NULL,
  `year` int(4) DEFAULT NULL,
  `lang` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL,
  `category_id` int(11) NOT NULL COMMENT 'Категорія',
  `subcategory_id` int(11) DEFAULT NULL COMMENT 'Підкатегорія',
  `price` float UNSIGNED NOT NULL COMMENT 'Ціна',
  `publisher` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Видавництво',
  `genre` text COLLATE utf8_unicode_ci COMMENT 'Жанр',
  `hide` enum('Так','Ні') COLLATE utf8_unicode_ci NOT NULL DEFAULT 'Так' COMMENT 'Приховано',
  `available` enum('Так','Ні') COLLATE utf8_unicode_ci NOT NULL DEFAULT 'Так' COMMENT 'В наявності',
  `created_at` int(11) NOT NULL COMMENT 'Створено',
  `updated_at` int(11) NOT NULL COMMENT 'Відредаговано'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

