INSERT INTO `goods` (`id`, `title`, `keywords`, `description`, `articul`, `url_key`, `author`, `year`, `lang`, `category_id`, `subcategory_id`, `price`, `publisher`, `genre`, `hide`, `available`, `created_at`, `updated_at`) VALUES
(1, 'Острів проклятих', 'Острів проклятих, Денніс Лігейн, Shutter Island, 9786171503519', 'В Ешекліфській лікарні для божевільних злочинців, що розташована на ізольованому острові, стається неочікуване: з палати втікає небезпечна вбивця...', '2401685', 'ostriv-prokljatih', 'Денніс Лігейн', 2023, 'українська', 1, NULL, 210, 'Клуб Сімейного Дозвілля', 'містичний детектив, кіноромани і екранізації', 'Ні', 'Так', 1702408381, 1702409126),
(2, 'Теплі історії зимової ночі', 'Теплі історії зимової ночі, дива, подарунки, День святого Миколая, Різдво, Новий рік', 'Узимку всі чекають на свята, дива й подарунки. Не завжди все йде як по маслу, але відважні українські діти допомагають казковим героям подолати зло й брехню та попри все відсвяткувати День святого Миколая, Різдво та Новий рік...', '978617', 'tepli-istorii-zimovoi-nochi', NULL, 2023, 'українська', 3, NULL, 550, 'Ранок', 'казки, міфи', 'Ні', 'Так', 1702409933, 1702451943),
(3, 'Хроніки Амбера. У 2-х томах. Том 2. П’ятикнижжя Мерліна', 'Хроніки Амбера, П’ятикнижжя Мерліна, Роджер Желязни', 'Здавалось би, після закінчення війни між двома головними світами — Амбером і Хаосом — у всьому Всесвіті запанував спокій...', '1060578', 'hroniki-ambera-u-2-h-tomah-tom-2-p-jatiknizhzhja-merlina', 'Роджер Желязни', 2020, 'українська', 4, NULL, 599, 'Навчальна книга - Богдан', 'класична проза, фентезі, сучасне фентезі, магічний реалізм', 'Ні', 'Так', 1702410668, 1702410861),
(4, 'Кобзар', 'Кобзар, Тарас Шевченко, Т. Г. Шевченко, ', 'Представлена збірка поезій Т. Г. Шевченка є спробою подати повний, справжній, канонічний текст його творів написаних з 1837 по 1861 р.р...', '2255334', 'kobzar', 'Тарас Шевченко', 2020, 'українська', 5, NULL, 852, 'Школяр', 'лірика, поезія', 'Ні', 'Так', 1702411435, 1702411597),
(5, 'Мій дідусь був черешнею', 'Мій дідусь був черешнею, Анджела Нанетті', 'Ця книга - родинна історія хлопчика Тоніно, розказана від його імені. Проста і казкова водночас. Така, що зачепить кожного знайомими ситуаціями, подіями, фразами і страхами.', '6791126', 'kniga-mij-didus-buv-chereshneju', 'Анджела Нанетті', 2015, 'українська', 3, NULL, 300, 'Видавництво Старого Лева', 'класика дитячої літератури, класична проза', 'Ні', 'Так', 1702411990, 1702412169),
(6, 'Триста поезій', 'Триста поезій, Ліна Костенко', 'До книги Ліни Костенко — улюбленої української поетеси кінця другого–початку третього тисячоліття — увійшли найвідоміші її вірші з різних періодів творчості — від ранньої поезії до сьогодні, а також уривки з романів та поем.', '5850350', 'trista-poezij', 'Ліна Костенко', 2019, 'українська', 5, NULL, 350, 'А-ба-ба-га-ла-ма-га', 'лірика, поезія', 'Ні', 'Так', 1702446565, 1702446752),
(7, 'Повернення до кафе на краю світу', 'Повернення до кафе на краю світу, Джон Стрелекі, Джон П. Стрелекі, Джон, Джессіка, Джон і Джессіка', 'Життя Джона назавжди змінилося після того, як він заблукав і потрапив у маленьке затишне кафе «Чому ви тут?». Чоловік провів там усю ніч і полишив це місце з новим поглядом на сенс буття загалом і на своє життя зокрема.', '9822468', 'povernennja-do-kafe-na-kraju-svitu', 'Джон П. Стрелекі', 2021, 'українська', 6, NULL, 176, 'Vivat', 'мотивація, персональні трансформації, сучасна проза, книги для самореалізації, практичні книги', 'Ні', 'Так', 1702447115, 1702447327),
(8, 'Фактор Черчилля. Як одна людина змінила історію', 'Черчель, джонсонюк, Боріс Джонсон, Борис Джонсон', 'Книжка Боріса Джонсона — це історія про те, як ексцентричний геній Черчилля, прем’єр-міністра Великої Британії, одного з найвизначніших лідерів ХХ століття...', '9427960', 'faktor-cherchillja-jak-odna-ljudina-zminila-istoriju', 'Боріс Джонсон', 2015, 'українська', 7, NULL, 333, 'Vivat', 'біографія, світова історія, історія успіху', 'Ні', 'Так', 1702447794, 1702447943),
(9, 'Твій милий Скрудж', 'Твій милий Скрудж, Меган Куїнн', 'Хіба можна не любити той різдвяний час, коли скрізь мерехтять гірлянди, кружляють сніжинки, пахне печивом і пряниками, коли хочеться одягатися в кумедні светри з дзвіночками, а святковий настрій, здається, витає в повітрі?', '4482339', 'tvij-milij-skrudzh', 'Меган Куїнн', 2023, 'українська', 1, NULL, 112, 'Видавництво Старого Лева', 'любовний роман, сучасна проза, романтична комедія', 'Ні', 'Так', 1702448277, 1702448443),
(10, 'Книга Кузьма Скрябін. Проза', 'Кузьма Скрябін, Андрій Кузьменко', 'Кузьма Скрябін (справжнє ім’я Андрій Кузьменко) народився в місті Самбір Львівської області 17 серпня 1968 року...', '0374119', 'kuz-ma-skrjabin-proza', 'Кузьма Скрябін', 2016, 'українська', 1, NULL, 181, 'Фоліо', 'сучасна проза, гумор і сатира', 'Ні', 'Так', 1702448923, 1702449045),
(11, 'Жовтий князь', 'Жовтий князь, Василь Барка, Василь Очерет, голодомор, геноцид, 1932-1933, 32-33', 'Роман «Жовтий князь» (1958—1961) — це перший в українській літературі великий прозовий твір, присвячений національній трагедії України — страшному голодомору 1932—1933 років, що забрав життя мільйонів людей.', '2375423', 'zhovtij-knjaz', 'Василь Барка', 2022, 'українська', 1, NULL, 160, 'Фоліо', 'класична проза, історична проза', 'Ні', 'Так', 1702449298, 1702449422),
(12, 'Василь Стус. Палімпсести', 'Василь Стус, Палімпсести', 'До книги Василя Стуса (6.01.1938–4.09.1985) — одного з найвидатніших українських поетів ХХ сто­ліття — увійшли вибрані вірші з різних періодів його творчості.', '5852194', 'vasil-stus-palimpsesti', 'Василь Стус', 2021, 'українська', 5, NULL, 350, 'А-ба-ба-га-ла-ма-га', 'поезія', 'Ні', 'Так', 1702449701, 1702449844),
(13, 'Книга 100 ідей для творчих мам', '100 ідей, мама, мати, дитина, творча мама', 'Ця книга — незамінна порадниця для мам діток від 2 до 8 років. Тут ви знайдете ідеї захопливих ігор на кухні та в дитячій кімнаті, поради, як перетворити звичайнісінькі продукти в пречудові матеріали для творчості, а також чимало цікавих експериментів.', '2109672', '100-idej-dlja-tvorchih-mam', NULL, 2020, 'українська', 6, NULL, 84, 'Талант', 'рукоділля і творчість', 'Ні', 'Так', 1702450136, 1702450276);

