INSERT INTO `slider` (`id`, `title`, `description`, `page_url_key`, `image`, `created_at`, `updated_at`) VALUES
(1, 'Вітаємо!', 'Завітайте до Книжкової вежі - вашого надійного провідника у світі книг. Дозвольте нам допомогти вам знайти те, що розширить ваші горизонти, захопить, збагатить і зробить ваші часи більш цікавими та захоплюючими.', 'shop', '8a05b540c0ca910fb5df46d3151f27f2.png', 1685051070, 1702396396),
(2, 'Хто ми такі?!', 'Книжкова вежа - це більше, ніж просто інтернет-магазин книг. Ми - команда ентузіастів, яка об\'єднала свою любов до слова, знань і пригод у єдине місце для всіх поціновувачів літератури.', 'about', 'edc67848cf704cab8bedde620857cf1d.png', 1685051799, 1702396411),
(3, 'Наші контакти', 'Книжкова вежа - це не лише магазин, але й спільнота книжкових любителів. Ми створюємо простір для обміну враженнями про прочитані книги, рекомендацій та дискусій. Приєднуйтесь до нашої спільноти, діліться своїми враженнями та знаходьте нових друзів серед однодумців.', 'contact', '915079bc81497b32bbd6f778642144e6.png', 1685051927, 1702396443);

